import React, { Component } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import { useSpring, animated } from "react-spring/web.cjs";
import PropTypes from "prop-types";
// web.cjs is required for IE 11 support

const useStyles = makeStyles((theme) => ({
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: "2px solid #000",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

const Fade = React.forwardRef(function Fade(props, ref) {
  const {
    in: open,
    children,
    onEnter,
    onExited,
    ...other
  } = props;
  const style = useSpring({
    from: { opacity: 0 },
    to: { opacity: open ? 1 : 0 },
    onStart: () => {
      if (open && onEnter) {
        onEnter();
      }
    },
    onRest: () => {
      if (!open && onExited) {
        onExited();
      }
    },
  });

  return (
    <animated.div ref={ref} style={style} {...other}>
      {children}
    </animated.div>
  );
});

Fade.propTypes = {
  children: PropTypes.element,
  in: PropTypes.bool.isRequired,
  onEnter: PropTypes.func,
  onExited: PropTypes.func,
};

function AddProjectForm() {
  return (
    <div className="row d-flex justify-content-center">
      <div className="col-md-6">
        <form className="text-center" action="#!">
          <p className="h4 mb-4">Add New Project</p>
          {/* id: projectName */}
          <div className="row">
            <div className="col">
              <input
                type="text"
                id="projectName"
                className="form-control"
                placeholder="Project Name"
                aria-describedby="projectName"
              />
              <small
                id="projectName"
                className="form-text text-muted mb-4"
              >
                It's just a first draft, no worries
              </small>
            </div>{" "}
            <div className="col">
              <input
                type="text"
                id="projectDeadline"
                className="form-control"
                placeholder="Project Deadline"
                aria-describedby="projectDeadlineSmall"
              />
              <small
                id="projectDeadlineSmall"
                className="form-text text-muted mb-4"
              >
                Otherwise, it never ends
              </small>
            </div>{" "}
          </div>

          {/* id: purpose */}
          <input
            type="text"
            id="purpose"
            className="form-control"
            placeholder="Purpose"
            aria-describedby="purposeInput"
          />
          <small
            id="purposeInput"
            className="form-text text-muted mb-4"
          >
            Write a quick mission statement.
          </small>
          {/* id: description */}
          <input
            type="textarea"
            id="description"
            className="form-control"
            placeholder="Description"
            aria-describedby="descriptionInput"
          />
          <small
            id="descriptionInput"
            className="form-text text-muted mb-4"
          >
            Technologies, Plans, Etc.
          </small>
          <div className="form-row mb-4">
            <div className="col">
              {/* id: friendOne */}
              <input
                type="text"
                id="friendOne"
                className="form-control"
                placeholder="Email of Friend One"
                aria-describedby="friendOneSmallText"
              />
              <small
                id="friendOneSmallText"
                className="form-text text-muted mb-4"
              >
                Who will keep you accountable?
              </small>
            </div>
            <div className="col">
              {/* id: friendTwo */}
              <input
                type="text"
                id="friendTwo"
                className="form-control"
                placeholder="Email of Friend Two"
                aria-describedby="friendTwoSmallText"
              />
              <small
                id="friendTwoSmallText"
                className="form-text text-muted mb-4"
              >
                Just in case
              </small>
            </div>
          </div>

          {/* ENDING FORM */}

          <button
            className="btn btn-info my-4 btn-block"
            type="submit"
          >
            Let's Execute
          </button>
        </form>
      </div>
    </div>
  );
}
export default function AddDeliverableModal({ name }) {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <button
        type="button"
        className="btn btn-outline-dark waves-effect"
        onClick={handleOpen}
      >
        {name}
      </button>
      <Modal
        aria-labelledby="spring-modal-title"
        aria-describedby="spring-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <DeliverableTable />
          </div>
        </Fade>
      </Modal>
    </div>
  );
}
