import React, { Component, useState } from "react";
import { format } from "date-fns";
import { enGB } from "date-fns/locale";
import { getDay } from "date-fns";
import "./calendar.css";
import {
  DateRangePickerCalendar,
  START_DATE,
} from "react-nice-dates";
import "react-nice-dates/build/style.css";
// Event Calendar Imports
import FullCalendar, {
  formatDate,
} from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
const modifiers = {
  //   disabled: (date) => getDay(date) === 6, // Disables Saturdays
  highlight: (date) => getDay(date) === 2, // Highlights Tuesdays
};
const modifiersClassNames = {
  highlight: "-highlight",
};
function DateRangePickerCalendarExample() {
  const [startDate, setStartDate] = useState();
  const [endDate, setEndDate] = useState();
  const [focus, setFocus] = useState(START_DATE);
  const handleFocusChange = (newFocus) => {
    setFocus(newFocus || START_DATE);
  };
  return (
    <div>
      {/* <p>Currently selecting: {focus}.</p> */}
      <DateRangePickerCalendar
        startDate={startDate}
        endDate={endDate}
        focus={focus}
        onStartDateChange={setStartDate}
        onEndDateChange={setEndDate}
        onFocusChange={handleFocusChange}
        locale={enGB}
        modifiers={modifiers}
        modifiersClassNames={modifiersClassNames}
      />
      <p>
        Selected start date:{" "}
        {startDate
          ? format(startDate, "dd MMM yyyy", {
              locale: enGB,
            })
          : "none"}
        .
      </p>
      <p>
        Selected end date:{" "}
        {endDate
          ? format(endDate, "dd MMM yyyy", { locale: enGB })
          : "none"}
        .
      </p>
    </div>
  );
}
let eventGuid = 0;
let todayStr = new Date().toISOString().replace(/T.*$/, ""); // YYYY-MM-DD of today

const INITIAL_EVENTS = [
  {
    id: createEventId(),
    title: "All-day event",
    start: todayStr,
  },
  {
    id: createEventId(),
    title: "Timed event",
    start: todayStr + "T12:00:00",
  },
  {
    id: createEventId(),
    title: "Finish the ",
    start: "2020-12-21" + "T12:00:00",
  },
];

function createEventId() {
  return String(eventGuid++);
}
function renderEventContent(eventInfo) {
  return (
    <>
      <b>{eventInfo.timeText}</b>
      <i>{eventInfo.event.title}</i>
    </>
  );
}

function renderSidebarEvent(event) {
  return (
    <li key={event.id}>
      <b>
        {formatDate(event.start, {
          year: "numeric",
          month: "short",
          day: "numeric",
        })}
      </b>
      <i>{event.title}</i>
    </li>
  );
}

class EventCalendar extends React.Component {
  state = {
    weekendsVisible: true,
    currentEvents: [],
  };
  renderSidebar() {
    return (
      <div className="demo-app">
        {this.renderSidebar()}
        <div className="demo-app-main">
          <div className="demo-app-sidebar">
            <div className="demo-app-sidebar-section">
              <h2>Instructions</h2>
              <ul>
                <li>
                  Select dates and you will be prompted to
                  create a new event
                </li>
                <li>Drag, drop, and resize events</li>
                <li>Click an event to delete it</li>
              </ul>
            </div>
            <div className="demo-app-sidebar-section">
              <label>
                <input
                  type="checkbox"
                  checked={this.state.weekendsVisible}
                  onChange={this.handleWeekendsToggle}
                ></input>
                toggle weekends
              </label>
            </div>
            <div className="demo-app-sidebar-section">
              <h2>
                All Events (
                {this.state.currentEvents.length})
              </h2>
              <ul>
                {this.state.currentEvents.map(
                  renderSidebarEvent
                )}
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
  handleWeekendsToggle = () => {
    this.setState({
      weekendsVisible: !this.state.weekendsVisible,
    });
  };
  handleDateSelect = (selectInfo) => {
    let title = prompt(
      "Please enter a new title for your event"
    );
    let calendarApi = selectInfo.view.calendar;

    calendarApi.unselect(); // clear date selection

    if (title) {
      calendarApi.addEvent({
        id: createEventId(),
        title,
        start: selectInfo.startStr,
        end: selectInfo.endStr,
        allDay: selectInfo.allDay,
      });
    }
  };
  handleEventClick = (clickInfo) => {
    if (
      alert(
        `Are you sure you want to delete the event '${clickInfo.event.title}'`
      )
    ) {
      clickInfo.event.remove();
    }
  };

  handleEvents = (events) => {
    this.setState({
      currentEvents: events,
    });
  };

  render() {
    return (
      <div>
        {" "}
        <FullCalendar
          plugins={[
            dayGridPlugin,
            timeGridPlugin,
            interactionPlugin,
          ]}
          headerToolbar={{
            left: "prev,next today",
            center: "title",
            right: "dayGridMonth,timeGridWeek,timeGridDay",
          }}
          initialView="dayGridMonth"
          editable={true}
          selectable={true}
          selectMirror={true}
          dayMaxEvents={true}
          weekends={this.state.weekendsVisible}
          initialEvents={INITIAL_EVENTS} // alternatively, use the `events` setting to fetch from a feed
          select={this.handleDateSelect}
          eventContent={renderEventContent} // custom render function
          eventClick={this.handleEventClick}
          eventsSet={this.handleEvents} // called after events are initialized/added/changed/removed
          /* you can update a remote database when these fire:
            eventAdd={function(){}}
            eventChange={function(){}}
            eventRemove={function(){}}
            */
        />
      </div>
    );
  }
}
export default class CalendarPage extends React.Component {
  render() {
    return (
      <div className="justify-content-center">
        <DateRangePickerCalendarExample />
        <EventCalendar />
      </div>
    );
  }
}
