import React, { Component, useState } from "react";
import { format } from "date-fns";
import { enGB } from "date-fns/locale";
import { getDay } from "date-fns";
import "./calendar.css";
import Calendar from "react-awesome-calendar";
import {
  DateRangePickerCalendar,
  START_DATE,
} from "react-nice-dates";
import "react-nice-dates/build/style.css";

const modifiers = {
  //   disabled: (date) => getDay(date) === 6, // Disables Saturdays
  highlight: (date) => getDay(date) === 2, // Highlights Tuesdays
};
const modifiersClassNames = {
  highlight: "-highlight",
};
function DateRangePickerCalendarExample() {
  const [startDate, setStartDate] = useState();
  const [endDate, setEndDate] = useState();
  const [focus, setFocus] = useState(START_DATE);
  const handleFocusChange = (newFocus) => {
    setFocus(newFocus || START_DATE);
  };
  return (
    <div>
      {/* <p>Currently selecting: {focus}.</p> */}
      <DateRangePickerCalendar
        startDate={startDate}
        endDate={endDate}
        focus={focus}
        onStartDateChange={setStartDate}
        onEndDateChange={setEndDate}
        onFocusChange={handleFocusChange}
        locale={enGB}
        modifiers={modifiers}
        modifiersClassNames={modifiersClassNames}
      />
      <p>
        Selected start date:{" "}
        {startDate
          ? format(startDate, "dd MMM yyyy", {
              locale: enGB,
            })
          : "none"}
        .
      </p>
      <p>
        Selected end date:{" "}
        {endDate
          ? format(endDate, "dd MMM yyyy", { locale: enGB })
          : "none"}
        .
      </p>
    </div>
  );
}

const events = [
  {
    id: 1,
    color: "#fd3153",
    from: "2019-05-02T18:00:00+00:00",
    to: "2019-05-05T19:00:00+00:00",
    title: "This is an event",
  },
  {
    id: 2,
    color: "#1ccb9e",
    from: "2019-05-01T13:00:00+00:00",
    to: "2019-05-05T14:00:00+00:00",
    title: "This is another event",
  },
  {
    id: 3,
    color: "#3694DF",
    from: "2019-05-05T13:00:00+00:00",
    to: "2019-05-05T20:00:00+00:00",
    title: "This is also another event",
  },
];

const props = {
  year: 2020,
  month: 12,
  day: 20,
};
export default class CalendarPage extends React.Component {
  render() {
    return (
      <div>
        <p>
          This page would highlight all the ongoing projects
          the user is currently going through{" "}
        </p>
        <div className="justify-content-center">
          <DateRangePickerCalendarExample />
          <Calendar events={events} props={props} />
        </div>
      </div>
    );
  }
}
