import {
  ADD_USER,
  DELETE_USER,
  LOGIN_FAILURE,
  LOGIN_SUCCESS,
  USER_DETAIL,
  LOGOUT_SUCCESS,
} from "./types";

export const addUser = (user) => ({
  type: ADD_USER,
  payload: user,
});

export const deleteUser = (id) => ({
  type: DELETE_USER,
  payload: id,
});

export const loginSuccess = () => ({
  type: LOGIN_SUCCESS,
});

export const loginFailure = (message) => ({
  type: LOGIN_FAILURE,
  message: message,
});

export const getUserDetail = (data) => ({
  type: USER_DETAIL,
  info: data,
});

export const logoutSuccess = () => ({
  type: LOGOUT_SUCCESS,
});
/**********************************************
 * Get User Information
 * ==================================
 ***********************************************/
export function getUserInfoThunk() {
  return (dispatch) => {
    return axios
      .get(`${process.env.REACT_APP_API_SERVER}/api/data`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem(
            "token"
          )}`,
        },
      })
      .then((response) => {
        if (response.data == null) {
          console.log("failure");
        } else {
          dispatch(getUserDetail(response.data));
        }
      })
      .catch((error) => {
        console.log("Error: ", error);
      });
  };
}

/**********************************************
 * Login User Thunk
 * ==================================
 * Input: email, password
 * Output: 
 *  1. 
 ***********************************************/
export function loginUserThunk(email, password) {
  return (dispatch) => {
    return axios
      .post(
        `${process.env.REACT_APP_API_SERVER}/api/login`,
        {
          email: email,
          password: password,
        }
      )
      .then((response) => {
        if (response.data == null) {
          dispatch(
            loginFailure("Didn't get any data back")
          );
        } else if (!response.data.token) {
          dispatch(loginFailure("Didn't get any token"));
        } else {
          localStorage.setItem(
            "token",
            response.data.token
          );
          dispatch(loginSuccess());
        }
      })
      .catch((error) => {
        console.log("Error: ", error);
      });
  };
}

/**********************************************
 * Logout Thunk
 * ==================================
 ***********************************************/

export function logoutThunk() {
  return (dispatch) => {
    localStorage.removeItem("token");
    dispatch(logoutSuccess());
  };
}
