import React, { Component, useState } from "react";
import "./components/calendar.css";
// Event Calendar Imports
import FullCalendar, {
  formatDate,
} from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

let eventGuid = 0;
let todayStr = new Date().toISOString().replace(/T.*$/, ""); // YYYY-MM-DD of today

const INITIAL_EVENTS = [
  {
    id: createEventId(),
    title: "User Interface",
    start: "2020-12-18" + "T12:00:00",
    end: "2020-12-20" + "T12:00:00",
  },
  {
    id: createEventId(),
    title: "Tests",
    start: "2020-12-21" + "T12:00:00",
    end: "2020-12-23" + "T12:00:00",
  },
  {
    id: createEventId(),
    title: "Database",
    start: "2020-12-24" + "T12:00:00",
    end: "2020-12-25" + "T12:00:00",
  },
  {
    id: createEventId(),
    title: "API",
    start: "2020-12-26" + "T12:00:00",
    end: "2020-12-28" + "T12:00:00",
  },
  {
    id: createEventId(),
    title: "Redux Store (State management)",
    start: "2020-12-29" + "T12:00:00",
    end: "2020-12-31" + "T12:00:00",
  },
  {
    id: createEventId(),
    title: "Authentication",
    start: "2021-01-01" + "T12:00:00",
    end: "2021-01-03" + "T12:00:00",
  },
  {
    id: createEventId(),
    title: "Payment",
    start: "2021-01-04" + "T12:00:00",
    end: "2021-01-06" + "T12:00:00",
  },
  {
    id: createEventId(),
    title: "Deployment",
    start: "2021-01-07" + "T12:00:00",
    end: "2021-01-09" + "T12:00:00",
  },
];

function createEventId() {
  return String(eventGuid++);
}
function renderEventContent(eventInfo) {
  return (
    <>
      <b>{eventInfo.timeText}</b>
      <i>{eventInfo.event.title}</i>
    </>
  );
}

function renderSidebarEvent(event) {
  return (
    <li key={event.id}>
      <div className="form-check">
        <input
          className="form-check-input"
          type="checkbox"
          value=""
          id={event.id}
        />
        <label
          className="form-check-label"
          for={event.id}
        ></label>
      </div>
      {formatDate(event.start, {
        year: "numeric",
        month: "short",
        day: "numeric",
      })}
      :<i>{event.title}</i>
    </li>
  );
}

class EventCalendar extends React.Component {
  state = {
    weekendsVisible: true,
    currentEvents: [],
  };
  renderSidebar() {
    return (
      <div className="demo-app-sidebar">
        <div className="demo-app-sidebar-section">
          <h2>
            Current Project: <br />
            Fight Procrastination! (
            {this.state.currentEvents.length} deliverables)
          </h2>
          <ul>
            {this.state.currentEvents.map(
              renderSidebarEvent
            )}
          </ul>
        </div>
      </div>
    );
  }
  handleWeekendsToggle = () => {
    this.setState({
      weekendsVisible: !this.state.weekendsVisible,
    });
  };
  handleDateSelect = (selectInfo) => {
    let title = prompt(
      "Please enter a new title for your event"
    );
    let calendarApi = selectInfo.view.calendar;

    calendarApi.unselect(); // clear date selection

    if (title) {
      calendarApi.addEvent({
        id: createEventId(),
        title,
        start: selectInfo.startStr,
        end: selectInfo.endStr,
        allDay: selectInfo.allDay,
      });
    }
  };
  handleEventClick = (clickInfo) => {
    if (
      alert(
        `Are you sure you want to delete the event '${clickInfo.event.title}'`
      )
    ) {
      clickInfo.event.remove();
    }
  };

  handleEvents = (events) => {
    this.setState({
      currentEvents: events,
    });
  };

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-3">
            {this.renderSidebar()}
          </div>
          <div className="col-9">
            <FullCalendar
              plugins={[
                dayGridPlugin,
                timeGridPlugin,
                interactionPlugin,
              ]}
              headerToolbar={{
                left: "prev,next today",
                center: "title",
                right:
                  "dayGridMonth,timeGridWeek,timeGridDay",
              }}
              initialView="dayGridMonth"
              editable={true}
              selectable={true}
              selectMirror={true}
              dayMaxEvents={true}
              weekends={this.state.weekendsVisible}
              initialEvents={INITIAL_EVENTS} // alternatively, use the `events` setting to fetch from a feed
              select={this.handleDateSelect}
              eventContent={renderEventContent} // custom render function
              eventClick={this.handleEventClick}
              eventsSet={this.handleEvents} // called after events are initialized/added/changed/removed
              /* you can update a remote database when these fire:
            eventAdd={function(){}}
            eventChange={function(){}}
            eventRemove={function(){}}
            */
            />
          </div>
        </div>
        <br />
        <div className="row">
          <h2>Instructions</h2>
        </div>
        <div className="row">
          <ul>
            <li>
              Select dates and you will be prompted to
              create a new event
            </li>
            <li>Drag, drop, and resize events</li>
            <li>Click an event to delete it</li>
          </ul>
        </div>
      </div>
    );
  }
}
export default class CalendarPage extends React.Component {
  render() {
    return (
      <div className="justify-content-center font3">
        <EventCalendar />
      </div>
    );
  }
}
