import {
  ADD_USER,
  DELETE_USER,
  LOGIN_FAILURE,
  LOGIN_SUCCESS,
  USER_DETAIL,
} from "./types";

export const addUser = (user) => ({
  type: ADD_USER,
  payload: user,
});

export const deleteUser = (id) => ({
  type: DELETE_USER,
  payload: id,
});

export const loginSuccess = () => ({
  type: LOGIN_SUCCESS,
});

export const loginFailure = (message) => ({
  type: LOGIN_FAILURE,
  message: message,
});

export const getUserDetail = (data) => ({
  type: USER_DETAIL,
  info: data,
});

export function getUserInfoThunk () {
    return ( dispatch ) => {
        return axios.get(`${process.env.REACT_APP_API_SERVER}`)
    }
}
