import React, { Component } from "react";
import AddTaskModal from "./AddBiteSizedTask";
import TimerModal from "./TimerModal";
import ArrowUp from "../../components/icons/ArrowUp";
import ArrowDown from "../../components/icons/ArrowDown";

function TableItem({ done, item }) {
  return (
    <tr>
      <th scope="row">
        {done === "true" && (
          <div className="form-check">
            <input
              className="form-check-input"
              type="checkbox"
              value=""
              id={item}
              checked
            />
            <label className="form-check-label" for={item}>
              <i>Done?</i>
            </label>
          </div>
        )}{" "}
        {done !== "true" && (
          <div className="form-check">
            <input
              className="form-check-input"
              type="checkbox"
              value=""
              id={item}
            />
            <label className="form-check-label" for={item}>
              <i>Done?</i>
            </label>
          </div>
        )}
      </th>
      <td>{item}</td>
      <td>
        <ArrowUp />
        <ArrowDown />
      </td>
      <td>
        <TimerModal name="Start 25 minute timer" />
      </td>
    </tr>
  );
}

const initialState = [
  {
    id: 1,
  },
];
export default class TaskBreakdownTable extends Component {
  loadTasks = (tasks) => {
    let taskList = [];
    for (let i = 0; i < tasks.length; i++) {
        console.log( tasks[ i ] );
        taskList.push(
            <TableItem />
        )
    }
  };
  render() {
    return (
      <div className="card">
        <div className="card-body">
          <h4 className="card-title">
            <strong>Task Breakdown</strong>
          </h4>{" "}
          <h5 className="blue-text pb-2">
            <strong>Make it into bite sized pieces </strong>
          </h5>
          <p className="card-text">
            Break a task into 100 sections. Note what
            percentage is done. For a 1,500 word report,
            every sentence is pretty much 1%.
          </p>
          <AddTaskModal name="Add Bite Sized Task" />
          <table className="table table-sm">
            <thead>
              <tr>
                <th scope="col">Done?</th>
                <th scope="col">Task</th>

                <th scope="col">Sort</th>

                <th scope="col">Timer</th>
              </tr>
            </thead>
            <tbody>{/* Each Task here */}</tbody>
          </table>
        </div>
      </div>
    );
  }
}

